@RestController
public class GradeController {

        @PostMapping("/grades")
        public ResponseEntity create(@RequestParam("code") String code, @RequestParam("nb_heures") BigDecimal nbHeuresBase, @RequestParam("taux") BigDecimal tauxBase) {
            Grade grade = new Grade();
            grade.setCode(code);
            grade.setNbHeuresBase(nbHeuresBase);
            grade.setTauxBase(tauxBase);
            this.gradeRepo.save(grade);

            URI uri = MvcUriComponentsBuilder
                    .fromMethodCall(
                            MvcUriComponentsBuilder.on(DemoWebApiController.class).findGrade(grade.getCode())
                    ).build().encode().toUri();

            // Réponse
            //      Code = 201
            //      Entête Location = 
            //      Corps = objet Grade
            return ResponseEntity.created(uri).body(grade);
        }

        @GetMapping("/grades/{code}")
        public ResponseEntity findGrade(@PathVariable String code) {
            return ResponseEntity.ok(this.gradeRepo.findByCode(code));
        }
}

